mapper.select:
select count(1) as dangers from owner_unit_danger d 
where d.form_id = #{formId} and d.unit_id = #{unitId} 

	and d.unit_area_id = #{unitAreaId}


	and d.building_id = #{buildingId}

select count(1) as dangers from owner_unit_danger d 
where d.form_code = #{code} and d.unit_id = #{unitId} 

	and d.unit_area_id = #{unitAreaId}


	and d.building_id = #{buildingId}
