mapper.select:
select t.* from (select o.id, o.name, o.layers, o.address, o.high_risk_type, 
o.district, o.street, o.community, o.hamlet, a.dict_label as district_name, b.dict_label as street_name, c.dict_label as community_name, d.dict_label as hamlet_name,
(select count(1) from owner_unit_area a where a.unit_id = o.id and a.type = '2') as doors, 
(select count(1) from ( select ua.id, (select count(1) from owner_unit_danger d where d.unit_area_id = ua.id) as dangers from owner_unit_area ua where ua.unit_id = o.id ) t where t.dangers>0) as households,
(select count(1) as dangers from owner_unit_danger ud1 where ud1.unit_id = o.id and ud1.status = 0 ) as rectifications,
(select count(1) as dangers from owner_unit_danger ud2 where ud2.unit_id = o.id and ud2.status = 1 ) as reexaminations,
(select count(1) as dangers from owner_unit_danger ud3 where ud3.unit_id = o.id and ud3.status = 2 ) as finishs,
(select count(1) from owner_unit_danger t where t.unit_id = o.id ) as dangers,
o.project_id, CASE WHEN r.detect_status is null THEN 0 ELSE r.detect_status END as status 

from owner_unit o left join owner_unit_report r on (r.unit_id = o.id and r.type = '1' )
left join sys_dict_data a on (o.district = a.dict_value and a.dict_type='district' and a.project_id = #{projectId})
left join sys_dict_data b on (o.street = b.dict_value and b.dict_type='street' and b.project_id = #{projectId})
left join sys_dict_data c on (o.community = c.dict_value and c.dict_type='community' and c.project_id = #{projectId})
left join sys_dict_data d on (o.hamlet = d.dict_value and d.dict_type='hamlet' and d.project_id = #{projectId})

where o.project_id = #{projectId} and o.type=#{type}


	
	 and exists (select w.id from project_worker_area w where w.worker_id = #{workerId} and type= '1' and w.district = o.district and w.street = o.street)
	
	
	 and exists (select w.id from project_worker_area w where w.worker_id = #{workerId} and type= '1' and w.district = o.district and w.street = o.street and w.community = o.community)
	
	
	and exists (select w.id from project_worker_area w where w.worker_id = #{workerId} and type= '1' and w.district = o.district and w.street = o.street and w.community = o.community and w.hamlet = o.hamlet )
	


) t where 1=1

	and t.district = #{district}


	and t.street = #{street}


	and t.community = #{community}


	and t.hamlet = #{hamlet}


	and t.status = #{status}


	and t.high_risk_type = #{highRiskType}


	and (t.name like concat('%', #{keyword}, '%') or t.address like concat('%', #{keyword}, '%'))

order by t.id asc

		select t.* from (select o.id, o.name, o.address, o.high_risk_type, o.district, o.street, o.community, o.hamlet, a.dict_label as district_name,b.dict_label as street_name,c.dict_label as community_name,d.dict_label as hamlet_name,
		(select count(1) as dangers from owner_unit_danger ud1 where ud1.unit_id = o.id and ud1.status = 0 ) as rectifications,
		(select count(1) as dangers from owner_unit_danger ud2 where ud2.unit_id = o.id and ud2.status = 1 ) as reexaminations,
		(select count(1) as dangers from owner_unit_danger ud3 where ud3.unit_id = o.id and ud3.status = 2 ) as finishs,
		(select count(1) from owner_unit_danger t where t.unit_id = o.id ) as dangers, 
		CASE WHEN ur.detect_status is null THEN 0 ELSE ur.detect_status END as status,
		o.project_id
		from owner_unit o 
		left join owner_unit_report ur on (ur.unit_id = o.id and ur.type = '2')
		left join sys_dict_data a on (o.district = a.dict_value and a.dict_type='district' and a.project_id =  #{projectId})
		left join sys_dict_data b on (o.street = b.dict_value and b.dict_type='street' and b.project_id =  #{projectId})
		left join sys_dict_data c on (o.community = c.dict_value and c.dict_type='community' and c.project_id =  #{projectId})
		left join sys_dict_data d on (o.hamlet = d.dict_value and d.dict_type='hamlet' and d.project_id =  #{projectId})
		where o.project_id = #{projectId} and o.type=#{type}
		
		
			
			 and exists (select w.id from project_worker_area w where w.worker_id = #{workerId} and type= '1' and w.district = o.district and w.street = o.street)
			
			
			 and exists (select w.id from project_worker_area w where w.worker_id = #{workerId} and type= '1' and w.district = o.district and w.street = o.street and w.community = o.community)
			
			
			and exists (select w.id from project_worker_area w where w.worker_id = #{workerId} and type= '1' and w.district = o.district and w.street = o.street and w.community = o.community and w.hamlet = o.hamlet )
			
		
		
		) t where t.dangers > 0

		
			and t.district = #{district}
		
		
			and t.street = #{street}
		
		
			and t.community = #{community}
		
		
			and t.hamlet = #{hamlet}
		
		
			and t.status = #{status}
		
		
			and t.high_risk_type = #{highRiskType}
		
		
			and (t.name like concat('%', #{keyword}, '%') or t.address like concat('%', #{keyword}, '%'))
		
		order by t.id asc
