mapper.select:
WITH danger_stats AS ( SELECT unit_id, rounds, COUNT(*) AS total_dangers, SUM(CASE WHEN status = '0' THEN 1 ELSE 0 END) AS rectifications, SUM(CASE WHEN status = '1' THEN 1 ELSE 0 END) AS reexaminations, SUM(CASE WHEN status = '2' THEN 1 ELSE 0 END) AS finishs, SUM(CASE WHEN status IS NOT NULL AND status <> '9' AND status <> '' THEN 1 ELSE 0 END) AS dangers FROM owner_unit_danger GROUP BY unit_id, rounds ), area_dangers AS ( SELECT a.unit_id, COUNT(DISTINCT d.unit_area_id) AS households FROM owner_unit_danger d JOIN owner_unit_area a ON d.unit_area_id = a.id and a.type = '2' GROUP BY a.unit_id ), piles AS ( SELECT unit_id, rounds, COUNT(*) AS piles FROM charging_pile GROUP BY unit_id, rounds )
SELECT o.id, o.name, o.layers, o.address, o.high_risk_type, o.rounds, o.detect_module, o.temperature, o.humidity, o.district, o.street, o.community, o.hamlet, a.dict_label AS district_name, b.dict_label AS street_name, c.dict_label AS community_name, d.dict_label AS hamlet_name,
o.door_number AS doors, COALESCE(ah.households, 0) AS households, COALESCE(ds.rectifications, 0) AS rectifications, COALESCE(ds.reexaminations, 0) AS reexaminations, COALESCE(ds.finishs, 0) AS finishs, COALESCE(ds.dangers, 0) AS dangers, COALESCE(pl.piles, 0) AS piles,
o.project_id, r.is_danger_notice, r.is_complete, r.is_test, r.is_test_reason, CASE WHEN r.detect_status IS NULL THEN 0 ELSE r.detect_status END AS status
FROM owner_unit o
LEFT JOIN owner_unit_report r ON r.unit_id = o.id AND r.type = '1'
LEFT JOIN sys_dict_data a ON o.district = a.dict_value AND a.dict_type = 'district' AND a.project_id = o.project_id LEFT JOIN sys_dict_data b ON o.street = b.dict_value AND b.dict_type = 'street' AND b.project_id = o.project_id LEFT JOIN sys_dict_data c ON o.community = c.dict_value AND c.dict_type = 'community' AND c.project_id = o.project_id LEFT JOIN sys_dict_data d ON o.hamlet = d.dict_value AND d.dict_type = 'hamlet' AND d.project_id = o.project_id
LEFT JOIN area_dangers ah ON ah.unit_id = o.id LEFT JOIN danger_stats ds ON ds.unit_id=o.id AND ds.rounds=o.rounds LEFT JOIN piles pl ON pl.unit_id = o.id AND pl.rounds = o.rounds
WHERE o.project_id = #{projectId} AND o.type = #{type}
AND EXISTS ( SELECT 1 FROM project_worker_area w WHERE w.worker_id = #{workerId} AND w.type = '1' AND w.district = o.district AND w.street = o.street )
AND EXISTS ( SELECT 1 FROM project_worker_area w WHERE w.worker_id = #{workerId} AND w.type = '1' AND w.district = o.district AND w.street = o.street AND w.community = o.community )
AND EXISTS ( SELECT 1 FROM project_worker_area w WHERE w.worker_id = #{workerId} AND w.type = '1' AND w.district = o.district AND w.street = o.street AND w.community = o.community AND w.hamlet = o.hamlet )
AND o.district = #{district}
AND o.street = #{street}
AND o.community = #{community}
AND o.hamlet = #{hamlet}
AND (CASE WHEN r.detect_status IS NULL THEN 0 ELSE r.detect_status END) = #{status}
AND o.high_risk_type = #{highRiskType}
AND (o.name LIKE CONCAT('%', #{keyword}, '%') OR o.address LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY o.id DESC

select t.* from (select o.id, o.name, o.address, o.high_risk_type, o.rounds, o.detect_module, o.district, o.street, o.community, o.hamlet, a.dict_label as district_name,b.dict_label as street_name,c.dict_label as community_name,d.dict_label as hamlet_name, (select count(1) as dangers from owner_unit_danger ud1 where ud1.unit_id = o.id and ud1.status = '0' and ud1.rounds = o.rounds) as rectifications, (select count(1) as dangers from owner_unit_danger ud2 where ud2.unit_id = o.id and ud2.status = '1' and ud2.rounds = o.rounds) as reexaminations, (select count(1) as dangers from owner_unit_danger ud3 where ud3.unit_id = o.id and ud3.status = '2' and ud3.rounds = o.rounds) as finishs, (select count(1) from owner_unit_danger t where t.unit_id = o.id and t.rounds = o.rounds and t.status <> '9' and t.status <> '' and t.status is not null ) as dangers, (select count(1) as piles from charging_pile cp where cp.unit_id = o.id and cp.rounds = o.rounds ) as piles, CASE WHEN ur.detect_status is null THEN 0 ELSE ur.detect_status END as status, o.project_id from owner_unit o left join owner_unit_report ur on (ur.unit_id = o.id and ur.type = '2') left join sys_dict_data a on (o.district = a.dict_value and a.dict_type='district' and a.project_id = o.project_id) left join sys_dict_data b on (o.street = b.dict_value and b.dict_type='street' and b.project_id = o.project_id) left join sys_dict_data c on (o.community = c.dict_value and c.dict_type='community' and c.project_id = o.project_id) left join sys_dict_data d on (o.hamlet = d.dict_value and d.dict_type='hamlet' and d.project_id = o.project_id) where o.project_id = #{projectId} and o.type=#{type}
and exists (select w.id from project_worker_area w where w.worker_id = #{workerId} and type= '1' and w.district = o.district and w.street = o.street)
and exists (select w.id from project_worker_area w where w.worker_id = #{workerId} and type= '1' and w.district = o.district and w.street = o.street and w.community = o.community)
and exists (select w.id from project_worker_area w where w.worker_id = #{workerId} and type= '1' and w.district = o.district and w.street = o.street and w.community = o.community and w.hamlet = o.hamlet )
) t where t.dangers > 0
and t.district = #{district}
and t.street = #{street}
and t.community = #{community}
and t.hamlet = #{hamlet}
and t.rectifications > 0
and t.reexaminations > 0
and ( t.dangers > 0 and t.dangers = t.finishs)
and t.high_risk_type = #{highRiskType}
and (t.name like concat('%', #{keyword}, '%') or t.address like concat('%', #{keyword}, '%'))
order by t.id desc

select t.* from (select o.id, o.name, o.type, o.project_id, o.address, o.high_risk_type,
o.district, o.street, o.community, o.hamlet, a.dict_label as district_name, b.dict_label as street_name, c.dict_label as community_name, d.dict_label as hamlet_name,
CASE WHEN r.detect_status is null THEN 0 ELSE r.detect_status END as status,  
r.detect_data  
from owner_unit o 
left join owner_unit_report r on (r.unit_id = o.id and r.type = '1' )
left join sys_dict_data a on (o.district = a.dict_value and a.dict_type='district' and a.project_id = o.project_id)
left join sys_dict_data b on (o.street = b.dict_value and b.dict_type='street' and b.project_id = o.project_id)
left join sys_dict_data c on (o.community = c.dict_value and c.dict_type='community' and c.project_id = o.project_id)
left join sys_dict_data d on (o.hamlet = d.dict_value and d.dict_type='hamlet' and d.project_id = o.project_id)
where o.project_id = #{projectId} and o.gridman = #{userId} order by o.id desc
) t where 1=1
 and t.district = #{district} 
 and t.street = #{street} 
 and t.community = #{community} 
 and t.hamlet = #{hamlet} 
 and t.status = #{status} 
 and t.high_risk_type = #{highRiskType} 
 and date(t.detect_data) >= #{startDate} 
 and date(t.detect_data) <= #{endDate} 

	and (t.name like concat('%', #{keyword}, '%') or t.address like concat('%', #{keyword}, '%'))

order by t.id desc

select t.* from (select o.id, o.name, o.type, o.project_id, o.address, o.high_risk_type,
o.district, o.street, o.community, o.hamlet, a.dict_label as district_name, b.dict_label as street_name, c.dict_label as community_name, d.dict_label as hamlet_name,
CASE WHEN r.detect_status is null THEN 0 ELSE r.detect_status END as status,  
r.detect_data  
from owner_unit o 
left join owner_unit_report r on (r.unit_id = o.id and r.type = '1' )
left join sys_dict_data a on (o.district = a.dict_value and a.dict_type='district' and a.project_id = o.project_id)
left join sys_dict_data b on (o.street = b.dict_value and b.dict_type='street' and b.project_id = o.project_id)
left join sys_dict_data c on (o.community = c.dict_value and c.dict_type='community' and c.project_id = o.project_id)
left join sys_dict_data d on (o.hamlet = d.dict_value and d.dict_type='hamlet' and d.project_id = o.project_id)
where o.project_id = #{projectId} 
 and o.district = #{userDistrict} 
 and o.street = #{userStreet} 
 and o.community = #{userCommunity} 
 and o.hamlet = #{userHamlet} 
order by o.id desc
) t where 1=1
 and t.district = #{district} 
 and t.street = #{street} 
 and t.community = #{community} 
 and t.hamlet = #{hamlet} 
 and t.status = #{status} 
 and t.high_risk_type = #{highRiskType} 
 and date(t.detect_data) >= #{startDate} 
 and date(t.detect_data) <= #{endDate} 

	and (t.name like concat('%', #{keyword}, '%') or t.address like concat('%', #{keyword}, '%'))

order by t.id desc

select u.id, u.name ,r.id as report_id, u.address,
CASE WHEN r.detect_status is null THEN 0 ELSE r.detect_status END as status 
from owner_unit u
left join owner_unit_report r on (r.unit_id = u.id and r.type = '1' )
where u.project_id = #{projectId} 
 and u.district = #{district} 
 and u.street = #{street} 
 and u.community = #{community} 
 and u.hamlet = #{hamlet}

