 and u.high_risk_type = #{type} 
 and u.district = #{district} 
 and u.street = #{street} 
 and u.community = #{community} 
 and u.hamlet = #{hamlet} 
order by u.id desc

select u.id, u.name ,r.id as report_id, u.address,
CASE WHEN r.detect_status is null THEN 0 ELSE r.detect_status END as status 
from owner_unit u
left join owner_unit_report r on (r.unit_id = u.id and r.type = '1' )
where u.project_id = #{projectId} 
 and u.district = #{district} 
 and u.street = #{street} 
 and u.community = #{community} 
 and u.hamlet = #{hamlet} 
order by u.id desc

select count(1)
from owner_unit u
left join owner_unit_report r on (r.unit_id = u.id and r.type = '1' ) 
where u.project_id = #{projectId}  and date(r.detect_data) = CURDATE()
 and u.district = #{district} 
 and u.street = #{street} 
 and u.community = #{community} 
 and u.hamlet = #{hamlet}
